import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Paper,
  TextField,
  Button,
  Typography,
  Box,
  ToggleButtonGroup,
  ToggleButton,
  Alert,
  useTheme,
  alpha,
} from '@mui/material';
import { School, Person } from '@mui/icons-material';
import { motion } from 'framer-motion';
import axios from 'axios';
import Logo from '../assets/logo';

const Login = ({ setUser }) => {
  const theme = useTheme();
  const navigate = useNavigate();
  const [role, setRole] = useState('student');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5001/api/login', {
        username,
        password,
        role,
      });

      if (response.data.success) {
        setUser(response.data.user);
        navigate(`/${role}`);
      } else {
        setError(response.data.message);
      }
    } catch (err) {
      setError('An error occurred. Please try again.');
    }
  };

  return (
    <Container component="main" maxWidth="sm">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <Box
          sx={{
            marginTop: 4,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <motion.div
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{
              type: 'spring',
              stiffness: 260,
              damping: 20,
              delay: 0.2,
            }}
          >
            <Logo width={120} height={120} color={theme.palette.primary.main} />
          </motion.div>

          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.3 }}
          >
            <Typography 
              component="h1" 
              variant="h2" 
              sx={{ 
                mt: 2,
                mb: 4,
                fontWeight: 800,
                letterSpacing: 1,
                background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
                backgroundClip: 'text',
                WebkitBackgroundClip: 'text',
                color: 'transparent',
                textAlign: 'center',
                textTransform: 'uppercase',
                fontFamily: '"Montserrat", sans-serif',
                textShadow: `2px 2px 4px ${alpha(theme.palette.primary.main, 0.2)}`,
              }}
            >
              EagleEye
            </Typography>
          </motion.div>

          <Paper
            elevation={3}
            sx={{
              padding: 4,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              width: '100%',
              background: `linear-gradient(135deg, ${alpha(theme.palette.background.paper, 0.9)}, ${alpha(theme.palette.background.paper, 0.95)})`,
              backdropFilter: 'blur(10px)',
              border: `1px solid ${alpha(theme.palette.primary.main, 0.1)}`,
              borderRadius: 2,
            }}
          >
            <Typography component="h2" variant="h5" sx={{ mb: 3 }}>
              Sign In
            </Typography>

            {error && (
              <Alert severity="error" sx={{ width: '100%', mb: 2 }}>
                {error}
              </Alert>
            )}

            <ToggleButtonGroup
              color="primary"
              value={role}
              exclusive
              onChange={(e, newRole) => newRole && setRole(newRole)}
              sx={{ 
                mb: 3,
                '& .MuiToggleButton-root': {
                  px: 3,
                  py: 1,
                  borderRadius: '20px !important',
                  mx: 0.5,
                  '&.Mui-selected': {
                    background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.primary.dark})`,
                    color: 'white',
                    boxShadow: `0 4px 10px ${alpha(theme.palette.primary.main, 0.25)}`,
                  },
                },
              }}
            >
              <ToggleButton value="student">
                <Person sx={{ mr: 1 }} /> Student
              </ToggleButton>
              <ToggleButton value="teacher">
                <School sx={{ mr: 1 }} /> Teacher
              </ToggleButton>
            </ToggleButtonGroup>

            <form onSubmit={handleSubmit} style={{ width: '100%' }}>
              <TextField
                variant="outlined"
                margin="normal"
                required
                fullWidth
                label="Username"
                autoFocus
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
              <TextField
                variant="outlined"
                margin="normal"
                required
                fullWidth
                label="Password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <Button
                type="submit"
                fullWidth
                variant="contained"
                color="primary"
                sx={{ 
                  mt: 3, 
                  mb: 2,
                  py: 1.5,
                  background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
                  borderRadius: '30px',
                  textTransform: 'none',
                  fontSize: '1.1rem',
                  fontWeight: 600,
                  '&:hover': {
                    background: `linear-gradient(45deg, ${theme.palette.primary.dark}, ${theme.palette.secondary.dark})`,
                    boxShadow: `0 6px 20px ${alpha(theme.palette.primary.main, 0.4)}`,
                  },
                }}
              >
                Sign In
              </Button>
            </form>
          </Paper>
        </Box>
      </motion.div>
    </Container>
  );
};

export default Login;
