import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Grid,
  Paper,
  Typography,
  Button,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  Box,
  Chip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  alpha,
  useTheme,
} from '@mui/material';
import {
  Add,
  Edit,
  Delete,
  Warning,
  Assessment,
  School,
} from '@mui/icons-material';
import { motion } from 'framer-motion';
import axios from 'axios';

const TeacherDashboard = () => {
  const theme = useTheme();
  const navigate = useNavigate();
  const [quizzes, setQuizzes] = useState([]);
  const [activeQuizzes, setActiveQuizzes] = useState([]);
  const [selectedQuiz, setSelectedQuiz] = useState(null);
  const [showQuizDialog, setShowQuizDialog] = useState(false);
  const [showDeleteDialog, setShowDeleteDialog] = useState(false);
  const [newQuizTitle, setNewQuizTitle] = useState('');
  const [newQuizDuration, setNewQuizDuration] = useState('');

  useEffect(() => {
    fetchQuizzes();
    const interval = setInterval(fetchActiveQuizzes, 5000); // Poll for active quizzes
    return () => clearInterval(interval);
  }, []);

  const fetchQuizzes = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/teacher/quizzes');
      setQuizzes(response.data);
    } catch (error) {
      console.error('Error fetching quizzes:', error);
    }
  };

  const fetchActiveQuizzes = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/teacher/active-quizzes');
      setActiveQuizzes(response.data);
    } catch (error) {
      console.error('Error fetching active quizzes:', error);
    }
  };

  const handleCreateQuiz = async () => {
    try {
      await axios.post('http://localhost:5001/api/teacher/quizzes', {
        title: newQuizTitle,
        duration: parseInt(newQuizDuration),
      });
      setShowQuizDialog(false);
      setNewQuizTitle('');
      setNewQuizDuration('');
      fetchQuizzes();
    } catch (error) {
      console.error('Error creating quiz:', error);
    }
  };

  const handleDeleteQuiz = async () => {
    try {
      await axios.delete(`http://localhost:5001/api/teacher/quizzes/${selectedQuiz.id}`);
      setShowDeleteDialog(false);
      setSelectedQuiz(null);
      fetchQuizzes();
    } catch (error) {
      console.error('Error deleting quiz:', error);
    }
  };

  const handleEditQuiz = (quiz) => {
    navigate(`/teacher/questions/${quiz.id}`);
  };

  const handleViewResults = (quiz) => {
    navigate(`/teacher/results/${quiz.id}`);
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <Box sx={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center',
          mb: 4,
          background: `linear-gradient(135deg, ${alpha(theme.palette.primary.main, 0.05)}, ${alpha(theme.palette.secondary.main, 0.05)})`,
          p: 3,
          borderRadius: 3,
          boxShadow: `0 4px 20px ${alpha(theme.palette.primary.main, 0.1)}`
        }}>
          <Typography 
            variant="h4" 
            component="h1" 
            sx={{
              fontWeight: 700,
              background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
              backgroundClip: 'text',
              WebkitBackgroundClip: 'text',
              color: 'transparent',
              display: 'flex',
              alignItems: 'center',
              gap: 2
            }}
          >
            <School sx={{ fontSize: 40, color: theme.palette.primary.main }} />
            Teacher Dashboard
          </Typography>
          <Button
            variant="contained"
            onClick={() => setShowQuizDialog(true)}
            sx={{
              background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
              px: 3,
              py: 1.5,
              borderRadius: '30px',
              fontSize: '1rem',
              fontWeight: 600,
              textTransform: 'none',
              boxShadow: `0 4px 10px ${alpha(theme.palette.primary.main, 0.3)}`,
              '&:hover': {
                background: `linear-gradient(45deg, ${theme.palette.primary.dark}, ${theme.palette.secondary.dark})`,
                boxShadow: `0 6px 15px ${alpha(theme.palette.primary.main, 0.4)}`,
              },
            }}
          >
            <Add sx={{ mr: 1 }} /> Create New Quiz
          </Button>
        </Box>

        <Grid container spacing={3}>
          {/* Active Quizzes */}
          <Grid item xs={12}>
            <Paper elevation={3} sx={{ p: 2 }}>
              <Typography 
                variant="h6" 
                gutterBottom
                sx={{ 
                  fontWeight: 600,
                  color: theme.palette.primary.main,
                  display: 'flex',
                  alignItems: 'center',
                  gap: 1,
                  mb: 3
                }}
              >
                <Assessment /> Active Quizzes
              </Typography>
              <List>
                {activeQuizzes.map((quiz) => (
                  <ListItem key={quiz.id}>
                    <ListItemText
                      primary={
                        <Typography variant="h6" sx={{ fontWeight: 600, color: theme.palette.primary.main }}>
                          {quiz.title}
                        </Typography>
                      }
                      secondary={
                        <Typography variant="body2" sx={{ color: theme.palette.text.secondary, mt: 0.5 }}>
                          {quiz.activeStudents} students currently taking quiz
                        </Typography>
                      }
                    />
                    <Box sx={{ display: 'flex', gap: 1 }}>
                      {quiz.violations > 0 && (
                        <Chip
                          icon={<Warning />}
                          label={`${quiz.violations} violations`}
                          color="error"
                          sx={{ 
                            borderRadius: '20px',
                            '& .MuiChip-icon': {
                              color: theme.palette.error.main,
                            },
                          }}
                        />
                      )}
                      <Button
                        variant="contained"
                        onClick={() => navigate(`/teacher/monitor/${quiz.id}`)}
                        sx={{
                          borderRadius: '20px',
                          px: 3,
                          py: 1,
                          background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
                          textTransform: 'none',
                          fontWeight: 600,
                          '&:hover': {
                            background: `linear-gradient(45deg, ${theme.palette.primary.dark}, ${theme.palette.secondary.dark})`,
                            boxShadow: `0 4px 12px ${alpha(theme.palette.primary.main, 0.3)}`,
                          },
                        }}
                      >
                        Monitor Live
                      </Button>
                    </Box>
                  </ListItem>
                ))}
              </List>
            </Paper>
          </Grid>

          {/* All Quizzes */}
          <Grid item xs={12}>
            <Paper elevation={3} sx={{ p: 2 }}>
              <Typography variant="h6" gutterBottom>
                All Quizzes
              </Typography>
              <List>
                {quizzes.map((quiz) => (
                  <ListItem key={quiz.id}>
                    <ListItemText
                      primary={quiz.title}
                      secondary={`Duration: ${quiz.duration} minutes | Questions: ${quiz.questionCount}`}
                    />
                    <ListItemSecondaryAction>
                      <IconButton
                        edge="end"
                        onClick={() => handleEditQuiz(quiz)}
                        sx={{ mr: 1 }}
                      >
                        <Edit />
                      </IconButton>
                      <IconButton
                        edge="end"
                        onClick={() => handleViewResults(quiz)}
                        sx={{ mr: 1 }}
                      >
                        <Assessment />
                      </IconButton>
                      <IconButton
                        edge="end"
                        onClick={() => {
                          setSelectedQuiz(quiz);
                          setShowDeleteDialog(true);
                        }}
                      >
                        <Delete />
                      </IconButton>
                    </ListItemSecondaryAction>
                  </ListItem>
                ))}
              </List>
            </Paper>
          </Grid>
        </Grid>

        {/* Create Quiz Dialog */}
        <Dialog 
          open={showQuizDialog} 
          onClose={() => setShowQuizDialog(false)}
          PaperProps={{
            sx: {
              borderRadius: 3,
              boxShadow: `0 8px 32px ${alpha(theme.palette.primary.main, 0.2)}`,
            }
          }}
        >
          <DialogTitle sx={{ 
            pb: 1,
            background: `linear-gradient(135deg, ${alpha(theme.palette.primary.main, 0.05)}, ${alpha(theme.palette.secondary.main, 0.05)})`,
          }}>
            <Typography variant="h5" component="div" sx={{ 
              fontWeight: 600,
              color: theme.palette.primary.main,
              display: 'flex',
              alignItems: 'center',
              gap: 1
            }}>
              <Add /> Create New Quiz
            </Typography>
          </DialogTitle>
          <DialogContent sx={{ 
            mt: 2,
            minWidth: 400,
            background: `linear-gradient(135deg, ${alpha(theme.palette.primary.main, 0.02)}, ${alpha(theme.palette.secondary.main, 0.02)})`,
          }}>
            <TextField
              autoFocus
              margin="dense"
              label="Quiz Title"
              fullWidth
              value={newQuizTitle}
              onChange={(e) => setNewQuizTitle(e.target.value)}
              variant="outlined"
              sx={{ 
                mb: 3,
                '& .MuiOutlinedInput-root': {
                  borderRadius: 2,
                }
              }}
            />
            <TextField
              margin="dense"
              label="Duration (minutes)"
              type="number"
              fullWidth
              value={newQuizDuration}
              onChange={(e) => setNewQuizDuration(e.target.value)}
              variant="outlined"
              sx={{ 
                '& .MuiOutlinedInput-root': {
                  borderRadius: 2,
                }
              }}
            />
          </DialogContent>
          <DialogActions sx={{ 
            p: 3,
            background: `linear-gradient(135deg, ${alpha(theme.palette.primary.main, 0.05)}, ${alpha(theme.palette.secondary.main, 0.05)})`,
          }}>
            <Button 
              onClick={() => setShowQuizDialog(false)}
              sx={{ 
                borderRadius: '20px',
                px: 3,
                textTransform: 'none',
                fontWeight: 500
              }}
            >
              Cancel
            </Button>
            <Button 
              onClick={handleCreateQuiz} 
              variant="contained"
              sx={{
                borderRadius: '20px',
                px: 3,
                background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
                textTransform: 'none',
                fontWeight: 600,
                '&:hover': {
                  background: `linear-gradient(45deg, ${theme.palette.primary.dark}, ${theme.palette.secondary.dark})`,
                  boxShadow: `0 4px 12px ${alpha(theme.palette.primary.main, 0.3)}`,
                },
              }}
            >
              Create Quiz
            </Button>
          </DialogActions>
        </Dialog>

        {/* Delete Confirmation Dialog */}
        <Dialog open={showDeleteDialog} onClose={() => setShowDeleteDialog(false)}>
          <DialogTitle>Delete Quiz</DialogTitle>
          <DialogContent>
            <Typography>
              Are you sure you want to delete "{selectedQuiz?.title}"? This action cannot be undone.
            </Typography>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setShowDeleteDialog(false)}>Cancel</Button>
            <Button onClick={handleDeleteQuiz} color="error">
              Delete
            </Button>
          </DialogActions>
        </Dialog>
      </motion.div>
    </Container>
  );
};

export default TeacherDashboard;
